import React, { useState,useEffect } from "react";
import { useNavigate } from 'react-router-dom'

const Registration = () => {
    const [userid, setUserid] = useState("");
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const [error,setError] = useState({userid:"",name:"",email:"",password:""});

    const navigate = useNavigate();
    // useEffect(() => {
    //     const auth = localStorage.getItem('user');
    //     if (auth) {
    //         navigate('/')
    //     }
    // }, [])

    const collectData = async () => {
    //console.warn(name, email, password);
    let result = await fetch("http://localhost:8001/api/register", {
        method: 'post',
        body: JSON.stringify({ userid, name, email, password }),
        headers: {
            'Content-Type': 'application/json'
        }
    });
    result = await result.json();
    console.warn(result);
    localStorage.setItem("user", JSON.stringify(result.result))

    navigate('/')
    }

    function handleSubmit(e){
        e.preventDefault();
        if(name === ""){
            setError(prevError =>{
                console.log(prevError) //As we use => function console we cannot see the prevError so we use different approach here for name field alone
                return {
                ...prevError,
                name:"Name is required"
            };
            });
        }

        //once we fill the name we should clear the error message --do the same for other fields as well

        if(name !== ""){
            setError(prevError =>({
                ...prevError,
                name:""
            }));
        }   

        if(email === ""){
            setError(prevError => ({
                ...prevError,
                email:"Email is required"
            }))
        }
       
        if(password === ""){
            setError(prevError => ({
                ...prevError,
                password:"Password is required"
            }))
        }
        if(email !== ""){
            if(!email.match(/^([\w.%+-]+)@([\w-]+\.)+([\w]{2,})$/i)){
                setError(prevError => ({
                    ...prevError,
                    email:"Email is not valid"
                }))
            }
        }
        
        if(password !== ""){
            if(password.length < 8){
                setError(prevError => ({
                    ...prevError,
                    password:"Password should be atleast 8 characters"
                }))
            }
            if(!password.match(/[@#$%^&*()!]/)){
                setError(prevError => ({
                    ...prevError,
                    password:"Password should contain atleast one special character"
                }))
            }
        }
     
         
        
   

    }
  return (
    <>
    <form onSubmit={handleSubmit}>
     <div className="register">
            <h1>Register</h1>
            <input className="inputBox" type="text" placeholder="Enter UserID"
                value={userid} onChange={(e) => setUserid(e.target.value)}
            />
            <input className="inputBox" type="text" placeholder="Enter Name"
                value={name} onChange={(e) => setName(e.target.value)}
            />
            <input className="inputBox" type="text" placeholder="Enter Email"
                value={email} onChange={(e) => setEmail(e.target.value)}
            />
            <input className="inputBox" type="password" placeholder="Enter password"
                value={password} onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={collectData} className="appButton" type="button">Sign Up</button>
        </div>
    </form>
    </>
  );
};

export default Registration;
